mapper.select:
select * from p_member
where p_id = #{p_id} 

	and password = #{password}

select * from p_member
where name= #{name}

and tel = #{tel}

mapper.update:
update p_member
w_name = #{w_name}, businessnumber = #{businessnumber}, w_address = #{w_address}, w_d_address = #{w_d_address}, w_tel = #{tel}, p_info = #{p_info}, career = #{career}, speciality = #{speciality}
where p_id = #{p_id};
