mapper.select:
		   SELECT *
      			FROM ( SELECT ROWNUM RN, A.*
     				   FROM (SELECT R.R_NO ,PM.NAME,R.R_DATE ,S.STARTDATE,R.TIME AS TIME,R.CONTENT AS RCONTENT ,C.CONTENT AS RCCONTENT,FC.CONTENT AS PCONTENT, R.REFUSE
					   FROM  P_MEMBER PM, SERVICE S, RESERVATION R, PET P, F_CODE C, F_CODE FC
					   WHERE PM.P_ID = R.P_ID AND S.R_NO=R.R_NO AND P.PET_NO = R.PET_NO AND C.CODE = R.CODE AND P.CODE = FC.CODE AND P.M_ID = #{m_id}
       				  ) A)
      	WHERE RN BETWEEN (#{pageNum}-1)*#{amount}+1
		AND (#{pageNum})*#{amount}

mapper.update:
UPDATE 	RESERVATION 
SET 	CODE = 402
WHERE 	R_NO = #{rno}

UPDATE 	RESERVATION 
SET 	CODE = 404
WHERE 	R_NO = #{rno}

UPDATE 	RESERVATION 
SET 	CODE = 405
WHERE 	R_NO = #{rno}
